import matplotlib.pyplot as plt
from sklearn.metrics import mean_squared_error
from sklearn.model_selection import train_test_split
from sklearn import linear_model
import numpy as np
from sklearn.preprocessing import PolynomialFeatures


def plot_learning_curves(model, X, y):
    X_train, X_val, y_train, y_val = (
        train_test_split(X, y, test_size=0.2))
    train_errors, val_errors = [], []
    for m in range(1, len(X_train)):
        model.fit(X_train[:m], y_train[:m])
        y_train_predict = model.predict(X_train[:m])
        y_val_predict = model.predict(X_val)
        train_errors.append(mean_squared_error(y_train[:m], y_train_predict))
        val_errors.append(mean_squared_error(y_val, y_val_predict))
    plt.plot(np.sqrt(train_errors), "r-+", lw=2, label="train")
    plt.plot(np.sqrt(val_errors), "b-", lw=3, label="val")
    plt.legend()
    plt.ylim(0, 3.5)
    plt.show()

m = 100
X_init = 6 * np.random.rand(m, 1) - 5
y_init = 0.7 * X_init ** 2 + X_init + 3 + np.random.randn(m, 1)
lin_regressor = linear_model.LinearRegression()
plot_learning_curves(lin_regressor, X_init, y_init)

from sklearn.pipeline import Pipeline

polynomial_regression = Pipeline([
    ("poly_features",
     PolynomialFeatures(degree=10, include_bias=False)),
    ("lin_regressor",
     lin_regressor)
])
plot_learning_curves(polynomial_regression, X_init, y_init)